import {createGlobalStyle} from 'styled-components'

export const GlobalStyle
 = createGlobalStyle `
:root{
    --background: #d2b48c;
    --red: #e52e4d;
    --brown: #964b00;

    --green-light: #33CC95;

    --text-title: #363f5f;
    --text-body: #969cb3;

    --background: #d2b48c;
    --shape: #ffffff;
}

*{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
}

html{
    @media (max-width: 1080px) {
        font-size: 93.75% ;
    }
    @media (max-width: 720px) {
        font-size: 87.5%;
    }
}

body{
    background: var(--background);
    -webkit-font-smoothing: antialiased;

}

body, input, textarea, button{
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
}

h1, h2, h3, h4, h5, h6, strong{
    font-weight: 600;
}

button{
    cursor: pointer;
}

[disable]{
    opacity: 0.6;
    cursor: not-allowed;
}

`
